import React from "react"


function Slide(props) {

    const contentCards = props.object.content.map(item => {
        return(
            <div className="content-card">
                <img src={item.image}/>
                <h3>{item.title}</h3>
                <p>{item.body}</p>
                {item.link && <a href={item.link}>Visit</a>}
            </div>
        )
    })

    return(
        <div className="slide carousel-item">
            <h2>props.object.title</h2>
            {contentCards}

        </div>
    )
}

export default Slide
